import React, {useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export default function Searchbar(props) {

    const callSearchFunction = (e) => {
        e.preventDefault();
        props.search(props.searchValue);
      }

    return(
        <Form>
            <Form.Label>Search for a book</Form.Label>
                <Form.Control type="text" placeholder="Enter text" value={props.searchValue} onChange={props.handleChange} />


            <Button variant="primary" type="submit" onClick={callSearchFunction}>
                    Submit
            </Button>
        </Form>


    )
}
