    const renderAuthors = (authors) => {
        if (Array.isArray(authors)) {
           return authors.join(", ")
        } else if (!authors) {
            return "Unknown"
        } else {
            return(authors)
        }
    }

    return (
        <Container>
             <Searchbar
             searchTerm= {searchTerm}
             handleChange= {handleChange} 
             searchFunction= {searchFunction}
             />

        <Row className="justify-content-md-center" sm={12}>



